parameters.item/revops_country@odata.bind:
@if(
    or(
        empty(items('Apply_to_each')?['revops_country']),
        equals(items('Apply_to_each')?['revops_country'], null)
    ),
    if(
        or(
            equals(variables('company')?['countryguid'], null),
            empty(variables('company')?['countryguid'])
        ),
        null,
        concat('/revops_countries(', variables('company')?['countryguid'], ')')
    ),
    items('Apply_to_each')?['revops_country']
)

