        equals(items('Apply_to_each')?['revops_iso3166_2subdivisioncode'], null)
    ),
    if(
        and(
            not(equals(variables('company')?['countrycode'], null)),
            not(empty(variables('company')?['countrycode'])),
            not(equals(variables('company')?['stateorprovince'], null)),
            not(empty(variables('company')?['stateorprovince']))
        ),
        concat(variables('company')?['countrycode'], '-', variables('company')?['stateorprovince']),
        null
    ),
