parameters.item/address1_city:
@{if(
    or(
        empty(items('Apply_to_each')?['address1_city']),
        equals(items('Apply_to_each')?['address1_city'], null)
    ),
    if(
        equals(
            body('Parse_company')?['geo']?['city'],
            null
        ),
        body('Parse_person')?['geo']?['city'],
        body('Parse_company')?['geo']?['city']
    ),
    items('Apply_to_each')?['address1_city']
)}

