    equals(
        body('Parse_person')?['telephone1'],
        null
    ),
    if(
        equals(
            variables('person')?['phone'],
            null
        ),
        '',
        replace(replace(replace(replace(variables('person')?['phone'], '-', ' '), '(', ''), ')', ''), '.', ' ')
