parameters.item/address1_city:
@{if(
    equals(
        body('Lookup_Lead_created')?['address1_city'], 
        null
    ), 
    if(
        equals(
            variables('company')?['city'], 
            null
        ), 
        variables('person')?['city'],
        variables('company')?['city']
    ), 
    body('Lookup_Lead_created')?['address1_city']
)}

parameters.item/companyname:
@{if(
    equals(
        body('Lookup_Lead_created')?['companyname'], 
        null
    ), 
    if(
        equals(
            body('Parse_company')?['legalName'], 
            null
        ),
        body('Parse_person')?['employment']?['name'],
        body('Parse_company')?['legalName']
    ),
    body('Lookup_Lead_created')?['companyname']
)}

parameters.item/telephone2:
@{if(
    equals(
        body('Lookup_Lead_created')?['telephone2'],
        null
    ),
    if(
        equals(
            variables('company')?['phone'],
            null
        ),
        '',
        replace(replace(replace(replace(variables('company')?['phone'], '-', ' '), '(', ''), ')', ''), '.', ' ')
    ),
    body('Lookup_Lead_created')?['telephone2']
)}

